mapper.select:
SELECT o.order_number as orderNumber, d.product_name as productName, d.project_type as projectType, o.amount as amount, d.buy_num as buy_num, o.pay_status as payStatus, o.order_time as orderTime, o.status as status, d.spec_model as spec_model, d.main_image as main_image, REPLACE(u.account_name, SUBSTR(u.account_name,4,4), '****') as accountName FROM om_order o LEFT JOIN om_order_details d ON d.order_id = o.id LEFT JOIN um_user_account u ON u.id =o.buy_account LEFT JOIN pd_product_info p ON d.combo_project_id = p.id
AND CONCAT(IFNULL(o.order_number,''), IFNULL(d.product_name,''),IFNULL(u.account_name,'')) LIKE CONCAT('%',#{keyword,jdbcType=VARCHAR},'%')
AND d.project_type=#{productType,jdbcType=INTEGER}
AND o.status=#{status,jdbcType=INTEGER}
AND o.order_time BETWEEN #{beginDate} AND #{endDate}
AND o.buy_account=#{accid,jdbcType=INTEGER}
ORDER BY o.order_time DESC
